","keccak256":"0xf92515413956f529d95977adc9b0567d583c6203fc31ab1c23824c35187e3ddc","license":"MIT"},"contracts/BaseOrganization.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "./IOrganization.sol";

abstract contract BaseOrganization is IOrganization {
	string public _name;
	address private _owner;
	address public immutable _pet;
	mapping(address => bool) private _employees;

	constructor(address pet, address owner, string memory name) {
		_owner = owner;
		_employees[owner] = true;
		_pet = pet;
		_name = name;
	}

	function addEmployee(address employee) public onlyOwner {
		_employees[employee] = true;
	}

	function removeEmployee(address employeeAddress) public onlyOwner {
		delete _employees[employeeAddress];
	}

	function isOwner(address ownerAddress) public view returns (bool) {
		return _owner == ownerAddress;
	}

	function isEmployee(address employeeAddress) public view returns (bool) {
		return _employees[employeeAddress];
	}

	modifier onlyEmployee() {
		require(
			_employees[msg.sender] || msg.sender == _owner,
			"Must be an employee or owner to use this function"
		);
		_;
	}

	modifier onlyOwner() {
		require(
			msg.sender == _owner,
			"Must be the owner of this organization to use this function"
		);
		_;
	}
}
","keccak256":"0x138125a142ad1c1a13fe2069f6513777eb7820ddf149bfeab780b4f66ce5ca8c","license":"MIT"},"contracts/DecryptConsumer.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/Strings.sol";
import { FunctionsClient } from "@chainlink/contracts/src/v0.8/functions/v1_0_0/FunctionsClient.sol";
import { FunctionsRequest } from "@chainlink/contracts/src/v0.8/functions/v1_0_0/libraries/FunctionsRequest.sol";
import { Pet } from "./Pet.sol";

contract DecryptConsumer is FunctionsClient {
	using FunctionsRequest for FunctionsRequest.Request;
	event OCRResponse(bytes32 indexed requestId, bytes result, bytes err);

	string private calculationLogic;
	bytes32 private donId;
	Pet private immutable pet;

	constructor(
		address oracle,
		bytes32 _donId,
		string memory _calculationLogic,
		Pet _pet
	) FunctionsClient(oracle) {
		donId = _donId;
		calculationLogic = _calculationLogic;
		pet = _pet;
	}

	function reEncryptMedicalRecords(
		string[] memory args,
		uint32 gasLimit
	) public returns (bytes32) {
		FunctionsRequest.Request memory req;

		req.initializeRequest(
			FunctionsRequest.Location.Inline,
			FunctionsRequest.CodeLanguage.JavaScript,
			calculationLogic
		);

		req.addDONHostedSecrets(
			0, // secret index
			1716848112 // secret id
		);

		req.setArgs(args);

		bytes32 assignedReqID = _sendRequest(
			req.encodeCBOR(),
			231, // subscriptionId for chainlink
			gasLimit,
			donId
		);
		return assignedReqID;
	}

	/**
	 * @notice Callback that is invoked once the DON has resolved the request or hit an error
	 *
	 * @param requestId The request ID, returned by sendRequest()
	 * @param response Aggregated response from the user code
	 * @param err Aggregated error from the user code or from the execution pipeline
	 * Either response or error parameter will be set, but never both
	 */
	function fulfillRequest(
		bytes32 requestId,
		bytes memory response,
		bytes memory err
	) internal override {
		pet.eventsDecryted(requestId, response, err);
	}
}
