","keccak256":"0x3bb6900ab0bcef13933bc2e2d733a2d442cf2a53276d8a397faf272d2cae958b","license":"MIT"},"contracts/OrganizationFactory.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "../enums/OrganizationType.sol";
import "./BaseOrganization.sol";
import "./organizationImpl/AnimalShelter.sol";
import "./OrganizationRegistry.sol";
import "./Pet.sol";

contract OrganizationFactory {	
	OrganizationRegistry private _registry;
	Pet private _pet;
	address private immutable _deployer;
	address[] private _deployedOrganizations;

	constructor() {
		_deployer = msg.sender;
	}

	function setRegistry(OrganizationRegistry registry) public deployerOnly() {
		_registry = registry;
	}

	function setPet(Pet pet) public deployerOnly() {
		_pet = pet;
	}

	function getPet() public view returns(address) {
		return address(_pet);
	}

	function getRegistry() public view returns(address){
		return address(_registry);
	}

	function getDeployedOrgs() public view returns(address[] memory){
		return _deployedOrganizations;
	}

	function createOrganization(
		OrganizationType organizationType,
		string memory name,
		address owner
	) public returns (address) {
		if (organizationType == OrganizationType.AnimalShelter) {
			AnimalShelter org = new AnimalShelter(_pet, owner, name);
			_registry.registerOrganization(org);
			_deployedOrganizations.push(address(org));
			return address(org);
		}
		revert("Org type not found");
	}

	modifier deployerOnly() {
		require(msg.sender == _deployer,
			"This method can only be called by the admin"
		);
		_;
	}
}
","keccak256":"0x69c6920c0ac584cf32a8749b74b337f1683b20608ecd561f686a507982bf7afd","license":"MIT"},"contracts/OrganizationRegistry.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import { IOrganization } from "./IOrganization.sol";
import { OrganizationFactory } from "./OrganizationFactory.sol";
import { Pet } from "./Pet.sol";

contract OrganizationRegistry {
	event OrganizationRegistered(IOrganization);
    event OrganizationValiditySet(IOrganization, bool);
	mapping(IOrganization => bool) private _orgs;
    address private immutable _admin;
    OrganizationFactory private immutable _factory;

	constructor(address admin, OrganizationFactory factory) {
        _admin = admin;        
        _factory = factory;
    }

    function isAdmin(address addr) public view returns (bool) {
        return _admin == addr;
    }

    function getOrganizationFactory() public view returns (OrganizationFactory) {
        return _factory;
    }

	//Keep the type IOrg for validation of type
	function registerOrganization(IOrganization org) factoryOnly() public {
        emit OrganizationRegistered(org);
		_orgs[org] = false;
	}

	function isValidated(IOrganization org) public view returns (bool) {
		return _orgs[org];
	}

    function isOrgMember(IOrganization org) public view returns (bool) {
        return org.isEmployee(msg.sender) || org.isOwner(msg.sender);
    }

    function setOrganizationValidity(IOrganization org, bool orgValidity) adminOnly() public {
        emit OrganizationValiditySet(org, orgValidity);
        _orgs[org] = orgValidity;
    }

    modifier adminOnly() {
        require(
            msg.sender == _admin,
            "Only the admin of this contract can all this method"
        );
        _;
    }

    modifier factoryOnly() {
        require(
            msg.sender == address(_factory),
            "Only the Organization Factory contract can call this method"
        );
        _;
    }
}
","keccak256":"0x85a50c29f2b29f05831a7b317caa3876e406c8c313f4ce8fa580e286fdd83f85","license":"MIT"},"contracts/Pet.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import { ERC721 } from "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";
import { OrgAffilliation } from "../models/OrgAffilliation.sol";
import { OrganizationRegistry } from "./OrganizationRegistry.sol";
import { IOrganization } from "./IOrganization.sol";
import { DecryptConsumer } from "./DecryptConsumer.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract Pet is ERC721 {
	event MintClaimMade(
		address orgAffiliation,
		address claimee,
		address prospectOwner
	);
	event FoundClaimMade(
		uint256 tokenId,
		address orgAffiliation,
		address claimee
	);
	event MedicalPayload(
		address orgAffiliation,
		address claimee,
		address prtOwner,
		string medPayload,
		uint256 tokenId, 
		bytes32 requestId
	);
	event reEncryptResponse(bytes32 indexed requestId, bytes result, bytes err);

	uint256 private _nextTokenId;
	OrganizationRegistry private immutable _orgRegistry;
	DecryptConsumer public immutable _decryptConsumer;
	address private immutable _pawledgerAddress;

	mapping(uint256 => OrgAffilliation) _foundClaimMap;

	constructor(
		OrganizationRegistry orgRegistry,
		address functionRouter,
		// bytes32 donId,
		string memory calculationLogic,
		address pawledgerAddress
	) ERC721("Pet", "PET") {
		_orgRegistry = orgRegistry;
		_pawledgerAddress = pawledgerAddress;
		_decryptConsumer = new DecryptConsumer(
			functionRouter,
			"fun-polygon-amoy-1",
			calculationLogic,
			this
		);
	}

	function getDecryptContract() public view returns(DecryptConsumer) {
		return _decryptConsumer;
	}

	function _baseURI() internal pure override returns (string memory) {
		return "metaservice uri here";
	}

	function getNextId() public view returns (uint256) {
		return _nextTokenId;
	}

	function mint(
		OrgAffilliation memory orgAff,
		address prospectOwner
	) public onlyValidOrg returns (uint256) {
		emit MintClaimMade(orgAff.org, orgAff.claimee, prospectOwner);
		uint256 tokenId = _nextTokenId++;

		//TODO validate prospect owner can recieve prior to this call
		_safeMint(prospectOwner, tokenId);

		return tokenId;
	}

	function adoptAnimal(
		address to,
		uint256 tokenId,
		string memory transfereePublicKey,
		uint32 gasLimit) public onlyValidOrg() {
			string[] memory args = new string[](4);
			args[0] = Strings.toString(tokenId);
			args[1] = transfereePublicKey;
			args[2] = Strings.toHexString(uint256(uint160(to)), 20);
			args[3] = Strings.toHexString(uint256(uint160(_pawledgerAddress)), 20);

			_decryptConsumer.reEncryptMedicalRecords(args, gasLimit);
			_safeTransfer(msg.sender, to, tokenId, "");
	}

	function foundPetClaim(
		uint256 tokenId,
		OrgAffilliation memory orgAff
	) public onlyValidOrg {
		emit FoundClaimMade(tokenId, orgAff.org, orgAff.claimee);

		_foundClaimMap[tokenId] = orgAff;
	}

	// FIXME add this back onlyOrgMember
	function receiveMedicalPayload(
		OrgAffilliation memory orgAff,
		address petOwner,
		string memory medPayload,
		uint256 tokenId,
		bytes32 requestId
	) public {
		emit MedicalPayload(orgAff.org, orgAff.claimee, petOwner, medPayload, tokenId, requestId);
	}

	//may want to enable anyone from the same org to be able to withdraw a claim
	function withdrawClaim(
		uint256 tokenId,
		address claimee
	) public onlyValidOrg {
		require(
			claimee == _foundClaimMap[tokenId].claimee,
			"Only the claimee can withdraw a claim"
		);

		delete _foundClaimMap[tokenId];
	}

	function rebukeClaim(uint256 tokenId) public {
		require(
			ownerOf(tokenId) == msg.sender,
			"Only the owner of the pet can rebuke a claim"
		);

		delete _foundClaimMap[tokenId];
	}

	function eventsDecryted(
		bytes32 requestId,
		bytes memory response,
		bytes memory err
	) public {
		emit reEncryptResponse(requestId, response, err);
	}

	modifier onlyValidOrg() {
		require(
			_orgRegistry.isValidated(IOrganization(msg.sender)),
			"Only a registered user of a validated organization can use this function"
		);
		_;
	}

	modifier onlyOrgMember() {
		require(
			_orgRegistry.isOrgMember(IOrganization(msg.sender)),
			"Only a registered user of an organization can use this function"
		);
		_;
	}
}
","keccak256":"0x49e7fe8824611f3d2f2d7f257d98613f3bc254d87d24bbe19332d262d3628238","license":"MIT"},"contracts/organizationImpl/AnimalShelter.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

// import "../BaseOrganization.sol";
import "../BaseOrganization.sol";
import "../Pet.sol";
import {IERC721Receiver} from "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "../../models/OrgAffilliation.sol";
import "../IOrganization.sol";

contract AnimalShelter is BaseOrganization {
	constructor(
		Pet pet,
		address owner,
		string memory name
	) BaseOrganization(address(pet), owner, name) {
	}

	function registerAnimal(address prospectiveOwner) public onlyEmployee() returns(uint256) {
		return Pet(_pet).mint(OrgAffilliation({org: address(this), claimee: msg.sender}), prospectiveOwner);
	}

	function sendMedicalInfo(address petOwner, string memory payload, uint256 tokenId, bytes32 requestId) public onlyEmployee() {
		Pet(_pet).receiveMedicalPayload(OrgAffilliation({org: address(this), claimee: msg.sender}), petOwner, payload, tokenId, requestId);
	}

	function foundPetClaim(uint256 tokenId) public onlyEmployee() {
		Pet(_pet).foundPetClaim(tokenId, OrgAffilliation({org: address(this), claimee: msg.sender}));
	}

	function adoptAnimal(uint256 tokenId, address user, string memory transfereePublicKey, uint32 gasLimit) public onlyEmployee() {
		Pet(_pet).adoptAnimal(user, tokenId, transfereePublicKey, gasLimit);
	}

	function onERC721Received(
		address operator,
		address from,
		uint256 tokenId,
		bytes calldata data
	) public returns (bytes4) {
		return IERC721Receiver.onERC721Received.selector;
	}
}
